// Simple subtraction in programming
let totalApples = 5;
let givenAway = 2;
let remaining = totalApples - givenAway;
console.log(remaining); // Output: 3

// Generate sequence in code
for (let i = 2; i <= 10; i = i + 2) {
  console.log(i);
}
// Output: 2, 4, 6, 8, 10

// Days of week in programming
const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
let currentDay = 0; // Monday is index 0
let afterDays = 3;
let newDay = (currentDay + afterDays) % 7;
console.log(days[newDay]); // Output: Thursday

// Counting items in programming
let redBalls = 3;
let blueBalls = 2;
let totalBalls = redBalls + blueBalls;
console.log('Total balls:', totalBalls); // Output: Total balls: 5

// Using arrays
let bag = ['red', 'red', 'red', 'blue', 'blue'];
console.log('Count:', bag.length); // Output: Count: 5

// Shapes in programming (Object-Oriented)
class Square {
  constructor(side) {
    this.sides = 4;
    this.sideLength = side;
    this.angles = 90; // degrees
  }

  area() {
    return this.sideLength * this.sideLength;
  }

  perimeter() {
    return 4 * this.sideLength;
  }
}

let mySquare = new Square(5);
console.log('Sides:', mySquare.sides); // 4
console.log('Area:', mySquare.area()); // 25
console.log('Perimeter:', mySquare.perimeter()); // 20

// Comparison in programming
let heightA = 180; // cm
let heightB = 170; // cm
let heightC = 160; // cm

if (heightA > heightB && heightB > heightC) {
  console.log('C is shortest');
}

// Using array and sorting
let people = [
  {name: 'A', height: 180},
  {name: 'B', height: 170},
  {name: 'C', height: 160}
];

people.sort((a, b) => a.height - b.height);
console.log('Shortest:', people[0].name); // C

// Calculate remaining amount in code
let toyPrice = 50;
let moneyIHave = 30;
let moneyNeeded = toyPrice - moneyIHave;

if (moneyNeeded > 0) {
  console.log(`You need ₹${moneyNeeded} more`);
} else {
  console.log('You have enough money!');
}
// Output: You need ₹20 more

// Function to check affordability
function canAfford(price, money) {
  if (money >= price) {
    return `You can buy it! Change: ₹${money - price}`;
  } else {
    return `Need ₹${price - money} more`;
  }
}

console.log(canAfford(50, 30)); // Need ₹20 more
console.log(canAfford(50, 60)); // You can buy it! Change: ₹10

// Categorization in programming
let items = ['Cat', 'Dog', 'Cow', 'Chair'];
let animals = ['Cat', 'Dog', 'Cow', 'Lion', 'Tiger'];
let furniture = ['Chair', 'Table', 'Bed', 'Sofa'];

items.forEach(item => {
  if (animals.includes(item)) {
    console.log(`${item} is an animal`);
  } else if (furniture.includes(item)) {
    console.log(`${item} is furniture - ODD ONE OUT`);
  }
});
// Output: Cat is an animal
// Dog is an animal
// Cow is an animal
// Chair is furniture - ODD ONE OUT

// Using object types
const things = {
  Cat: 'animal',
  Dog: 'animal',
  Cow: 'animal',
  Chair: 'furniture'
};

for (let thing in things) {
  if (things[thing] !== 'animal') {
    console.log(`${thing} is the odd one (${things[thing]})`);
  }
}

// Multiplication in loops
let pagesPerDay = 10;
let days = 3;
let totalPages = pagesPerDay * days;
console.log(`Total pages: ${totalPages}`); // 30

// Using a loop to show daily progress
let total = 0;
for (let day = 1; day <= days; day++) {
  total = total + pagesPerDay;
  console.log(`Day ${day}: Read ${total} pages so far`);
}
// Output:
// Day 1: Read 10 pages so far
// Day 2: Read 20 pages so far
// Day 3: Read 30 pages so far

// Function for any number of days
function pagesRead(perDay, numDays) {
  return perDay * numDays;
}

console.log('In 7 days:', pagesRead(10, 7)); // 70
console.log('In 30 days:', pagesRead(10, 30)); // 300

// Storing data in programming (similar concept)

// BAD: Hard-coded values (like memorizing)
function callFriend() {
  let phone = '9876543210'; // Have to remember/update everywhere
  console.log('Calling', phone);
}

// BETTER: Store in a variable
const friendPhone = '9876543210';
function callFriend() {
  console.log('Calling', friendPhone); // Single source of truth
}

// BEST: Store in a data structure (like phone contacts)
const contacts = {
  'John': '9876543210',
  'Sarah': '9876543211',
  'Mike': '9876543212'
};

function callFriend(name) {
  if (contacts[name]) {
    console.log(`Calling ${name}: ${contacts[name]}`);
  } else {
    console.log('Contact not found');
  }
}

callFriend('John'); // Calling John: 9876543210

// BEST PRACTICE: Use database or file storage
// In real apps, use: localStorage, database, config files

// Addition in programming
let a = 5;
let b = 3;
let result = a + b;
console.log(result); // Output: 8

// String concatenation (be careful!)
let x = '5';
let y = '3';
console.log(x + y); // Output: '53' (not 8!)
console.log(Number(x) + Number(y)); // Output: 8

// FOR LOOP - repeat a fixed number of times
for (let i = 1; i <= 5; i++) {
  console.log('Hello ' + i);
}
// Output: Hello 1, Hello 2, Hello 3, Hello 4, Hello 5

// WHILE LOOP - repeat while condition is true
let count = 1;
while (count <= 5) {
  console.log('Count: ' + count);
  count++;
}

// Real example: Print multiplication table
for (let i = 1; i <= 10; i++) {
  console.log(`5 × ${i} = ${5 * i}`);
}

// Storing ocean data in programming
const oceans = [
  { name: 'Pacific', size: 165200000, rank: 1 },
  { name: 'Atlantic', size: 106460000, rank: 2 },
  { name: 'Indian', size: 70560000, rank: 3 },
  { name: 'Southern', size: 20327000, rank: 4 },
  { name: 'Arctic', size: 14090000, rank: 5 }
];

// Find largest ocean
let largest = oceans[0];
for (let ocean of oceans) {
  if (ocean.size > largest.size) {
    largest = ocean;
  }
}
console.log('Largest:', largest.name); // Pacific

// Sort by size
oceans.sort((a, b) => b.size - a.size);
console.log('Biggest:', oceans[0].name); // Pacific

// File saving in programming concepts

// JavaScript (Browser)
function saveData() {
  let data = document.getElementById('textInput').value;
  // Save to browser storage
  localStorage.setItem('myWork', data);
  console.log('Saved!');
}

function loadData() {
  let saved = localStorage.getItem('myWork');
  if (saved) {
    document.getElementById('textInput').value = saved;
    console.log('Loaded!');
  }
}

// Save to file (Node.js)
const fs = require('fs');
let data = 'My work content';
fs.writeFileSync('mywork.txt', data);
console.log('File saved!');

// Auto-save (like Google Docs)
setInterval(() => {
  saveData();
  console.log('Auto-saved');
}, 30000); // Save every 30 seconds

// Start/Stop in programming

// Timer example
let timer;
function startTimer() {
  console.log('Timer STARTED');
  let seconds = 0;
  timer = setInterval(() => {
    seconds++;
    console.log(`Time: ${seconds}s`);
  }, 1000);
}

function stopTimer() {
  clearInterval(timer);
  console.log('Timer STOPPED');
}

// Process control
class GameEngine {
  start() {
    console.log('Game started');
    this.running = true;
    this.gameLoop();
  }

  stop() {
    console.log('Game stopped');
    this.running = false;
  }

  gameLoop() {
    if (this.running) {
      // Game logic here
      requestAnimationFrame(() => this.gameLoop());
    }
  }
}

// Loop control
for (let i = 0; i < 100; i++) {
  if (i === 50) {
    break; // STOP the loop
  }
  console.log(i);
}

solution1.code:
function checkNumber(num) {
  if (num > 0) {
    console.log(num + ' is positive');
  } else if (num < 0) {
    console.log(num + ' is negative');
  } else {
    console.log(num + ' is zero');
  }
}

// Test cases
checkNumber(5);   // 5 is positive
checkNumber(-3);  // -3 is negative
checkNumber(0);   // 0 is zero

solution2.code:
function checkNumber(num) {
  if (num > 0) {
    return 'positive';
  } else if (num < 0) {
    return 'negative';
  } else {
    return 'zero';
  }
}

// Test and use
let result = checkNumber(10);
console.log('The number is ' + result); // The number is positive

let numbers = [5, -3, 0, 10, -7];
numbers.forEach(n => {
  console.log(`${n} is ${checkNumber(n)}`);
});

solution3.code:
function checkNumber(num) {
  return num > 0 ? 'positive' : num < 0 ? 'negative' : 'zero';
}

// Test
console.log(checkNumber(5));   // positive
console.log(checkNumber(-3));  // negative
console.log(checkNumber(0));   // zero

